import React, { useState } from "react";
import styled from "styled-components";
import LogIn from "./LogIn";
import SignUp from "./SignUp";

const UserDiv = styled.div`
    min-width: 400px;
    min-height: 450px;
    border: 2px solid black;
    display: flex;
    flex-direction: column;
    justify-content: center;
    border-radius: 25px 25px 25px 25px;
    overflow: hidden;


    .user-selection {
      height: 45px;
      background-color: aliceblue;
      display: flex;
      flex-direction: row;
      width: 100%;
    }



    form {
      flex: 3;
      padding: 0px 50px;
      justify-content: center;
      margin-bottom: 40px;
    }

`

const UserSelDiv = styled.div`
         display: flex;
      justify-content: center;
      align-items: center;
      border: 1px solid black;
      flex: 1;
      cursor: pointer;
      background-color: ${props => props.selectedBgColor == "Log In" ? "green" : "aliceblue"};
`


export default function UserForm({formState}) {
  const [userTabState, setUserTabState] = useState(formState)
  const [selBgColor, setSelBgColor] = useState(["Log In", "Sign Up"])

  const handleFormState =(e) => {
    setUserTabState(e.target.innerHTML)
    setSelBgColor((prevColor)=> {
      if(prevColor === selBgColor) {
        return [...prevColor, selBgColor ]
      }
    })
    console.log(userTabState)
  
  }

  return (
    <UserDiv>
      <div className="user-selection">
        <UserSelDiv className="login" onClick={handleFormState}  selectedBgColor={selBgColor[0]}>Log In</UserSelDiv>
        <UserSelDiv className="signup" onClick={handleFormState} selectedBgColor={selBgColor[1]}>Sign Up</UserSelDiv>
      </div>
      {userTabState === "Log In" ?  <LogIn /> : <SignUp />}
    </UserDiv>
  );
}
